import requests
import json
from dotenv import load_dotenv
import os


# Access the API key from the environment variable
load_dotenv()
weather_api_key = os.getenv("API_KEY")

# Set the city name
city_name='East Lansing'

def get_weather(weather_api_key, city_name):

    # Set URL for API
    url='https://api.weatherapi.com/v1/forecast.json?key=%s&q=%s&days=2&aqi=yes&alerts=yes' % (weather_api_key, city_name)
   
    # Get data from API
    response = requests.get(url)
    data = response.json()

    return data

def get_weather_data(data):
    #from the sample json data extract the following information
    #current day and time
    #forecast data
        #for the current day only get data for hours that fall after the current time
        #for the next day get all data
        #data to include: time, temp_f, humidity, dewpoint_f, chance of rain, wind mph, gust mph, UV, and air quality
        #if the month is between 10 and 3, include the chance of snow
        #if there is an alert, include the alert message

    #current day and time
    current_time = data['location']['localtime']
    current_day = current_time.split()[0]

    #forecast data
    forecast_data = []
    for day in data['forecast']['forecastday']:
        if day['date'] == current_day:
            for hour in day['hour']:
                if hour['time'] >= current_time.split()[1]:
                    forecast_data.append(hour)
        else:
            forecast_data.extend(day['hour'])

    return forecast_data

